    private void criarCadastroAlunoButtonActionPerformed(java.awt.event.ActionEvent evt) {                                                         
        String nome = nomeAlunoTextField.getText();
        String serie = salaAlunoTextField.getText();
        String codMatricula = codMatriculaTextField.getText();
        if(nome.isBlank() || serie.isBlank() || codMatricula.isBlank()){
            JOptionPane.showMessageDialog(null, "É preciso preencher todos os campos!", "Quiz ArtVantGarde", 0);
        }else{
            try{
                UsuarioAluno aluno = new UsuarioAluno(nome, codMatricula);
                DAO dao = new DAO();
                if(dao.existeAluno(aluno)){
                    JOptionPane.showMessageDialog(null, "Já existe um cadastro com os dados inseridos!");
                }else{
                    dao.novoLogin(login);
                    int id = dao.pegarId(login);
                    aluno = new UsuarioAluno(nome, serie, codMatricula, id);
                    dao.cadastroAluno(aluno);
                    JOptionPane.showMessageDialog(null, "Cadastro criado com sucesso! Retornando à tela de Login!", "Quiz ArtVantGarde", 1);
                    TelaFazerLogin frame = new TelaFazerLogin();
                    this.dispose();
                    frame.setVisible(true);
                }
            }catch(Exception e){
                JOptionPane.showMessageDialog(null, "Problemas Técnicos!");
            }
        }
    }                                                        

