    private void criarLoginButtonActionPerformed(java.awt.event.ActionEvent evt) {                                                 
        String novoEmail = novoLoginTextField.getText();
        String novaSenha = new String(senhaPasswordField.getPassword());
        String confirmacaoSenha = new String(senhaConfirmaPasswordField.getPassword());
        
        if(novoEmail.isBlank() || novaSenha.isBlank() || confirmacaoSenha.isBlank()){
            JOptionPane.showMessageDialog(null, "O usuário já existe! Insira outro email!", "Quiz ArtVantGarde", 2);
        }
        if(novaSenha.equals(confirmacaoSenha)){
            try{
                Login login = new Login(novoEmail, novaSenha);
                // CriarLogin novoCadastro = new CriarLogin(novoEmail, novaSenha);
                DAO dao = new DAO();
                if(dao.existeLogin(novoEmail)){
                    JOptionPane.showMessageDialog(null, "O usuário já existe! Insira outro email!", "Quiz ArtVantGarde", 2);
                }else{
                    if(novoEmail.endsWith("@jpiaget.pro.com.br")){
                        TelaCadastroProfessor frame = new TelaCadastroProfessor(login);
                        this.dispose();
                        frame.setVisible(true);
                    }else if(novoEmail.contains("@")){
                        TelaCadastroAluno frame = new TelaCadastroAluno(login);
                        this.dispose();
                        frame.setVisible(true);
                    }else{
                        JOptionPane.showMessageDialog(null, "O método de login inserido é invalido, por favor certifique-se de que você inseriu um email.", "Quiz ArtVantGarde", 0);
                    }
                }
            }catch(Exception e){
                JOptionPane.showMessageDialog(null, "Problemas Técnicos!");
            }
        }else{
            JOptionPane.showMessageDialog(null, "As senhas inseridas são diferentes, certifique-se de inserir a mesma senha.", "Quiz ArtVantGarde", 0);
        }
    }                                                

    private void voltarButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                              
        TelaFazerLogin frame = new TelaFazerLogin();
        this.dispose();
        frame.setVisible(true);
    }                                             

    private void verSenhaLabelMouseClicked(java.awt.event.MouseEvent evt) {                                           
        verSenhaLabel.setVisible(false);
        esconderSenhaLabel.setVisible(true);
        senhaPasswordField.setEchoChar((char)0);
        senhaConfirmaPasswordField.setEchoChar((char)0);
    }                                          

    private void esconderSenhaLabelMouseClicked(java.awt.event.MouseEvent evt) {                                                
        verSenhaLabel.setVisible(true);
        esconderSenhaLabel.setVisible(false);
        senhaPasswordField.setEchoChar('*');
        senhaConfirmaPasswordField.setEchoChar('*');
    }                                               

